tinymce.PluginManager.add('imageupload', function(editor, url) {
	editor.addButton('imageupload', {
		tooltip: 'Upload an image',
		icon : 'image',
		text: 'Upload',
		onclick: function() {
			editor.windowManager.open({
                title: 'Upload an image',
                file : 'imagemanager/upload',
                width : 360,
                height: 310,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
			});
		}
	});
});